import { Component, OnInit } from '@angular/core';
import { Vingador } from './vingador';
import '../assets/css/style.css';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
    title: string;
    vingadores: Array<Vingador>;
    selecionado: Vingador;
    novo: Vingador = new Vingador(0, '', '', '');

    constructor() {
        this.title = 'Vingadores';
        this.vingadores = [
            new Vingador(1, 'Capitão América', 'Steve Rogers', 'http://cdn.movieweb.com/img.news.tops/NElzIT8Tgq7wot_1_b.jpg'),
            new Vingador(2, 'Viúva Negra', 'Natasha Romanoff','http://www.artengenho.com.br/wp-content/uploads/2016/05/viuva-negra.png'),
            new Vingador(3, 'Ms. Marvel', 'Carol Danvers', 'data:image/jpeg;base64,'),
            new Vingador(4, 'Deadpool', 'Wade Wilson','data:image/jpeg;base64,'),
            new Vingador(5, 'Gavião Arqueiro', 'Rambo','http://i.ndtvimg.com/mt/movies/2014-08/Sly-rambo-630.jpg')
        ];
    }

    ngOnInit(): void {
    }

    heroiSelecionado(vingador: Vingador): void {
        this.selecionado = vingador;
    }

    cadastrar(): void {
        const novoId: number = this.vingadores.length + 1;
        this.vingadores.push(new Vingador(novoId, this.novo.nome, this.novo.pessoa, this.novo.urlVingador));
        this.novo = new Vingador(0, '', '', '');
    }

    excluir(item): void {
        delete this.vingadores[item-1];
    }
    

}
